package com.gabriel.property.validator;

/**
 * Validator interface
 * <p>
 * A validators job is to check whether a given object can be converted
 * to the desired type. Moreover, a validator can check for additional requirements.
 */
public interface Validator {

    /**
     * Checks whether the object meets the criteria
     *
     * @param object Raw object coming from editor
     * @return True if the object meets the criteria and can be converted, false otherwise
     */
    public boolean validate(Object object);

}

package com.gabriel.property.validator.integer;

import com.gabriel.property.validator.Validator;

public class IntegerParityValidator implements Validator {

    protected boolean even;

    public IntegerParityValidator() {
        this(true);
    }

    public IntegerParityValidator(boolean even) {
        this.even = even;
    }

    public void setEven(boolean even) {
        this.even = even;
    }

    @Override
    public boolean validate(Object object) {
        int value = Integer.parseInt((String) object);

        if (even) {
            return value % 2 == 0;
        } else {
            return value % 2 != 0;
        }
    }
}

