        return isFloat(object);
    }

    protected boolean isFloat(Object object) {
        try {
            Float.parseFloat((String) object);
        } catch (NumberFormatException e) {
            return false;
        }

        return true;
    }
}

package com.gabriel.property.cell;

import com.gabriel.property.property.IntegerProperty;
import com.gabriel.property.util.Keys;

import javax.swing.*;
import java.awt.*;
import java.awt.event.KeyEvent;

public class IntegerCellComponent extends AbstractCellComponent {

    private IntegerProperty property;
    private JTextField textField;

    public IntegerCellComponent(IntegerProperty property) {
        this.property = property;
        this.textField = new JTextField();

        textField.addKeyListener(new KeyAdapter());
    }

    @Override
    public Component getTableCellEditorComponent(JTable jTable, Object o, boolean b, int i, int i1) {
        textField.setText(property.getValue().toString());

        return textField;
    }

    @Override
    public Object getCellEditorValue() {
        return property.getValue();
    }

    @Override
    public Component getTableCellRendererComponent(JTable jTable, Object o, boolean b, boolean b1, int i, int i1) {
        return new JLabel(property.getValue().toString());
    }

    private class KeyAdapter extends java.awt.event.KeyAdapter {
        @Override
        public void keyReleased(KeyEvent event) {
            if (Keys.ignoreKey(event.getKeyCode())) {
                return;
            }

            if (property.getValidator().validate(textField.getText())) {
                property.setValue(Integer.parseInt(textField.getText()));
                textField.setBackground(options.getBackgroundColor());
                eventDispatcher.dispatchUpdateEvent(property);
            } else {
                textField.setBackground(options.getInvalidColor());
            }
        }
    }
}

package com.gabriel.property.validator.integer;

import com.gabriel.property.validator.Validator;

/**
 * Default implementation to validate integer objects
 */
public class IntegerValidator implements Validator {

    @Override
    public boolean validate(Object object) {
        return isInteger(object);
    }

    protected boolean isInteger(Object object) {
        try {
            Integer.parseInt((String) object);
        } catch (NumberFormatException e) {
            return false;
        }

        return true;
    }
}

package com.gabriel.property.property.selection;

import  com.gabriel.property.property.AbstractProperty;
import com.gabriel.property.validator.NullValidator;

import java.util.ArrayList;

public class SelectionProperty<T> extends AbstractProperty<T> {

    protected ArrayList<Item<T>> items;

    public SelectionProperty(String name, ArrayList<Item<T>> items) {
        super(name, items.get(0).getValue(), new NullValidator());

        this.items = items;
    }

