package com.gabriel.draw.command;

import com.gabriel.drawfx.command.Command;
import com.gabriel.drawfx.model.Shape;

import java.lang.reflect.Method;

public class SetPropertyCommand implements Command {
    private final Shape shape;
    private final String propertyName;
    private final Object newValue;
    private final Object oldValue;
    private final String setterMethodName;
    private final String getterMethodName;

    public SetPropertyCommand(Shape shape, String propertyName, String setterMethodName, String getterMethodName, Object newValue) {
        this.shape = shape;
        this.propertyName = propertyName;
        this.setterMethodName = setterMethodName;
        this.getterMethodName = getterMethodName;
        this.newValue = newValue;

        try {
            Method getter = shape.getClass().getMethod(getterMethodName);
            this.oldValue = getter.invoke(shape);
        } catch (Exception e) {
            throw new RuntimeException("Failed to get old value for property: " + propertyName, e);
        }
    }

    @Override
    public void execute() {
        setProperty(newValue);
    }

    @Override
    public void undo() {
        setProperty(oldValue);
    }

    @Override
    public void redo() {
        execute();
    }

    private void setProperty(Object value) {
        try {
            Method setter = shape.getClass().getMethod(setterMethodName, value.getClass());
            setter.invoke(shape, value);
        } catch (Exception e) {
            try {
                Method setter = shape.getClass().getMethod(setterMethodName, int.class);
                setter.invoke(shape, value);
            } catch (Exception e2) {
                throw new RuntimeException("Failed to set property: " + propertyName, e2);
            }
        }
    }
}

package com.gabriel.draw.command;

import com.gabriel.drawfx.command.Command;
import com.gabriel.drawfx.model.Shape;
import com.gabriel.drawfx.service.AppService;

public class DeleteShapeCommand implements Command {
    private final AppService appService;
    private final Shape shape;

    public DeleteShapeCommand(AppService appService, Shape shape) {
        this.appService = appService;
        this.shape = shape;
    }

    @Override
    public void execute() {
        appService.getDrawing().getShapes().remove(shape);
    }

    @Override
    public void undo() {
        appService.getDrawing().getShapes().add(shape);
    }

    @Override
    public void redo() {
        execute();
    }
}

package com.gabriel.draw.command;

import com.gabriel.drawfx.command.Command;
import com.gabriel.drawfx.model.Shape;
import com.gabriel.drawfx.service.AppService;

import java.awt.Point;

public class MoveShapeCommand implements Command {
    private final AppService appService;
    private final Shape shape;
    private final Point start;
    private final Point end;
    private final int originalX;
    private final int originalY;

    public MoveShapeCommand(AppService appService, Shape shape, Point start, Point end) {
        this.appService = appService;
        this.shape = shape;
        this.start = new Point(start);
        this.end = new Point(end);
        this.originalX = shape.getLocation().x;
        this.originalY = shape.getLocation().y;
    }

    @Override
    public void execute() {
        int dx = end.x - start.x;
        int dy = end.y - start.y;
        shape.getLocation().x += dx;
        shape.getLocation().y += dy;
    }

    @Override
    public void undo() {
        shape.getLocation().x = originalX;
        shape.getLocation().y = originalY;
    }

    @Override
    public void redo() {
        execute();
    }
}

package com.gabriel.draw.command;

import com.gabriel.drawfx.command.Command;
import com.gabriel.drawfx.model.Shape;
import com.gabriel.drawfx.service.AppService;

import java.awt.Point;

public class ScaleShapeCommand implements Command {
    private final AppService appService;
    private final Shape shape;
    private final Point start;
    private final Point end;
    private final int originalX;
    private final int originalY;
    private final int originalWidth;
    private final int originalHeight;

    public ScaleShapeCommand(AppService appService, Shape shape, Point start, Point end) {
        this.appService = appService;
        this.shape = shape;
        this.start = new Point(start);
        this.end = new Point(end);
        this.originalX = shape.getLocation().x;
        this.originalY = shape.getLocation().y;
        this.originalWidth = shape.getWidth();
        this.originalHeight = shape.getHeight();
    }

    @Override
    public void execute() {
        appService.scale(shape, start, end);
    }

    @Override
    public void undo() {
        shape.getLocation().x = originalX;
        shape.getLocation().y = originalY;
        shape.setWidth(originalWidth);
        shape.setHeight(originalHeight);
    }

    @Override
    public void redo() {
        execute();
    }
}
